contracts/common/ContractRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {RoleManager} from "./RoleManager.sol";

/**
 * @title ContractRegistry
 * @notice Lightweight registry that stores the addresses of deployed EquiX modules.
 */
contract ContractRegistry is RoleManager {
    mapping(bytes32 => address) private _addresses;

    event AddressRegistered(bytes32 indexed key, address indexed value);

    constructor(address admin) RoleManager(admin) {}

    function setAddress(bytes32 key, address value) external onlyAdmin {
        _addresses[key] = value;
        emit AddressRegistered(key, value);
    }

    function getAddress(bytes32 key) external view returns (address) {
        return _addresses[key];
    }
}

contracts/common/DataTypes.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title DataTypes
 * @notice Shared structs and enums reused across the EquiXToken Capital smart-contract suite.
 */
library DataTypes {
    // -------------------- Property / Fraction Ownership --------------------
    struct FractionHolder {
        address holder;
        uint256 balance;
    }

    struct FractionSnapshot {
        uint256 totalShares;
        FractionHolder[] holders;
        uint256 timestamp;
    }

    // -------------------- Escrow --------------------
    enum EscrowStatus {
        NONE,
        CREATED,
        FUNDED,
        RELEASED,
        REFUNDED
    }

    struct EscrowCondition {
        bytes32 id;
        string description;
        bool satisfied;
        uint64 satisfiedAt;
    }

    struct Escrow {
        address buyer;
        address seller;
        address asset;
        uint256 assetId;
        address paymentToken; // address(0) for HBAR
        uint256 amount;
        EscrowStatus status;
        uint64 createdAt;
        uint64 fundedAt;
        uint64 releasedAt;
        EscrowCondition[] conditions;
    }

    // -------------------- Investment Offering --------------------
    enum OfferingStatus {
        NONE,
        ACTIVE,
        FILLED,
        CLOSED,
        FAILED
    }

    struct Contribution {
        address investor;
        uint256 amount;
    }

    struct Offering {
        uint256 targetAmount;
        uint256 minInvestment;
        uint256 maxInvestment;
        uint256 totalRaised;
        uint64 opensAt;
        uint64 closesAt;
        OfferingStatus status;
        uint256 escrowId;
        address propertyToken;
        uint256 propertyTokenId;
    }

    // -------------------- Governance --------------------
    enum ProposalStatus {
        NONE,
        ACTIVE,
        EXECUTED,
        EXPIRED,
        CANCELLED
    }

    struct Proposal {
        string description;
        uint64 createdAt;
        uint64 deadline;
        uint256 forVotes;
        uint256 againstVotes;
        ProposalStatus status;
        bytes callData;
        address target;
    }

    // -------------------- Registry / Deeds --------------------
    struct DeedMeta {
        uint256 propertyId;
        string hash;
        string[] signatures;
        uint64 createdAt;
        address creator;
    }

    struct PropertyRecord {
        uint256 propertyId;
        address currentOwner;
        string deedHash;
        uint64 registeredAt;
        uint64 updatedAt;
    }
}

contracts/common/EquixUtils.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

library EquixUtils {
    uint256 internal constant BPS_DENOMINATOR = 10_000;

    function toBps(uint256 numerator, uint256 denominator) internal pure returns (uint256) {
        if (denominator == 0) return 0;
        return (numerator * BPS_DENOMINATOR) / denominator;
    }

    function applyBps(uint256 value, uint256 bps) internal pure returns (uint256) {
        return (value * bps) / BPS_DENOMINATOR;
    }

    function requireNonZero(address account, string memory message) internal pure {
        require(account != address(0), message);
    }
}

contracts/common/RoleManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @title RoleManager
 * @notice Centralised role definitions shared across the EquiXToken Capital contracts.
 */
abstract contract RoleManager is AccessControl {
    bytes32 public constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");
    bytes32 public constant ESCROW_ROLE = keccak256("ESCROW_ROLE");
    bytes32 public constant ORACLE_ROLE = keccak256("ORACLE_ROLE");
    bytes32 public constant ISSUER_ROLE = keccak256("ISSUER_ROLE");
    bytes32 public constant GOVERNANCE_ROLE = keccak256("GOVERNANCE_ROLE");

    constructor(address admin) {
        _setupRole(DEFAULT_ADMIN_ROLE, admin);
        _setRoleAdmin(OPERATOR_ROLE, DEFAULT_ADMIN_ROLE);
        _setRoleAdmin(ESCROW_ROLE, DEFAULT_ADMIN_ROLE);
        _setRoleAdmin(ORACLE_ROLE, DEFAULT_ADMIN_ROLE);
        _setRoleAdmin(ISSUER_ROLE, DEFAULT_ADMIN_ROLE);
        _setRoleAdmin(GOVERNANCE_ROLE, DEFAULT_ADMIN_ROLE);
    }

    modifier onlyAdmin() {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "ROLE:ADMIN_ONLY");
        _;
    }

    modifier onlyOperator() {
        require(hasRole(OPERATOR_ROLE, _msgSender()), "ROLE:OPERATOR_ONLY");
        _;
    }

    modifier onlyEscrowAgent() {
        require(hasRole(ESCROW_ROLE, _msgSender()), "ROLE:ESCROW_ONLY");
        _;
    }

    modifier onlyOracle() {
        require(hasRole(ORACLE_ROLE, _msgSender()), "ROLE:ORACLE_ONLY");
        _;
    }

    modifier onlyIssuer() {
        require(hasRole(ISSUER_ROLE, _msgSender()), "ROLE:ISSUER_ONLY");
        _;
    }
}

contracts/deeds/DigitalTitleDeed.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {RoleManager} from "../common/RoleManager.sol";
import {DataTypes} from "../common/DataTypes.sol";

/**
 * @title DigitalTitleDeed
 * @notice Stores deed metadata, hashes, and signatures anchored to a property.
 */
contract DigitalTitleDeed is RoleManager {
    uint256 private _deedIdTracker;
    mapping(uint256 => DataTypes.DeedMeta) private _deeds;
    mapping(uint256 => uint256) private _propertyToDeed;

    event DeedCreated(uint256 indexed deedId, uint256 indexed propertyId, string hash);
    event DeedTransferred(uint256 indexed deedId, address indexed newOwner);
    event DeedVerified(uint256 indexed deedId, string hash);

    constructor(address admin) RoleManager(admin) {}

    function createDeed(uint256 propertyId, string calldata hash, string[] calldata signatures)
        external
        onlyOperator
        returns (uint256 deedId)
    {
        require(propertyId != 0, "DEED:PROPERTY_REQUIRED");
        require(bytes(hash).length > 0, "DEED:HASH_REQUIRED");

        deedId = ++_deedIdTracker;
        _deeds[deedId] = DataTypes.DeedMeta({
            propertyId: propertyId,
            hash: hash,
            signatures: signatures,
            createdAt: uint64(block.timestamp),
            creator: _msgSender()
        });
        _propertyToDeed[propertyId] = deedId;

        emit DeedCreated(deedId, propertyId, hash);
    }

    function updateSignatures(uint256 deedId, string[] calldata signatures) external onlyOperator {
        DataTypes.DeedMeta storage deed = _requireDeed(deedId);
        deed.signatures = signatures;
    }

    function transferDeed(uint256 deedId, address newOwner) external onlyOperator {
        require(newOwner != address(0), "DEED:INVALID_OWNER");
        DataTypes.DeedMeta storage deed = _requireDeed(deedId);
        deed.creator = newOwner;
        emit DeedTransferred(deedId, newOwner);
    }

    function verifyDeed(uint256 deedId) external view returns (string memory hash, string[] memory signatures) {
        DataTypes.DeedMeta storage deed = _requireDeed(deedId);
        return (deed.hash, deed.signatures);
    }

    function getDeed(uint256 deedId) external view returns (DataTypes.DeedMeta memory) {
        return _requireDeed(deedId);
    }

    function getDeedByProperty(uint256 propertyId) external view returns (uint256 deedId) {
        return _propertyToDeed[propertyId];
    }

    function _requireDeed(uint256 deedId) internal view returns (DataTypes.DeedMeta storage) {
        DataTypes.DeedMeta storage deed = _deeds[deedId];
        require(deed.propertyId != 0, "DEED:NOT_FOUND");
        return deed;
    }
}

contracts/escrow/EscrowManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {RoleManager} from "../common/RoleManager.sol";
import {DataTypes} from "../common/DataTypes.sol";

/**
 * @title EscrowManager
 * @notice Handles milestone-based escrows for property transfers and investments.
 */
contract EscrowManager is RoleManager, ReentrancyGuard {
    uint256 private _escrowIdTracker;
    mapping(uint256 => DataTypes.Escrow) private _escrows;

    event EscrowCreated(uint256 indexed escrowId, address indexed buyer, address indexed seller, uint256 amount, address paymentToken);
    event FundsDeposited(uint256 indexed escrowId, address indexed payer, uint256 amount);
    event ConditionUpdated(uint256 indexed escrowId, bytes32 conditionId, bool satisfied);
    event FundsReleased(uint256 indexed escrowId, address indexed to, uint256 amount);
    event Refunded(uint256 indexed escrowId, address indexed to, uint256 amount);

    address public immutable complianceOracle;

    constructor(address admin, address _complianceOracle) RoleManager(admin) {
        complianceOracle = _complianceOracle;
    }

    function getEscrow(uint256 escrowId) external view returns (DataTypes.Escrow memory) {
        return _escrows[escrowId];
    }

    function createEscrow(
        address buyer,
        address seller,
        uint256 amount,
        address paymentToken,
        address asset,
        uint256 assetId
    ) external onlyEscrowAgent returns (uint256 escrowId) {
        require(buyer != address(0) && seller != address(0), "ESCROW:INVALID_PARTY");
        require(amount > 0, "ESCROW:AMOUNT_ZERO");

        escrowId = ++_escrowIdTracker;
        DataTypes.Escrow storage esc = _escrows[escrowId];
        esc.buyer = buyer;
        esc.seller = seller;
        esc.amount = amount;
        esc.paymentToken = paymentToken;
        esc.asset = asset;
        esc.assetId = assetId;
        esc.status = DataTypes.EscrowStatus.CREATED;
        esc.createdAt = uint64(block.timestamp);

        emit EscrowCreated(escrowId, buyer, seller, amount, paymentToken);
    }

    function setConditions(uint256 escrowId, DataTypes.EscrowCondition[] calldata conditions) external onlyEscrowAgent {
        DataTypes.Escrow storage esc = _requireEscrow(escrowId);
        require(esc.status == DataTypes.EscrowStatus.CREATED || esc.status == DataTypes.EscrowStatus.FUNDED, "ESCROW:STATUS");

        delete esc.conditions;
        for (uint256 i = 0; i < conditions.length; i++) {
            esc.conditions.push(
                DataTypes.EscrowCondition({
                    id: conditions[i].id,
                    description: conditions[i].description,
                    satisfied: conditions[i].satisfied,
                    satisfiedAt: conditions[i].satisfied ? uint64(block.timestamp) : 0
                })
            );
            emit ConditionUpdated(escrowId, conditions[i].id, conditions[i].satisfied);
        }
    }

    function markCondition(uint256 escrowId, bytes32 conditionId, bool satisfied) external onlyOracle {
        DataTypes.Escrow storage esc = _requireEscrow(escrowId);
        for (uint256 i = 0; i < esc.conditions.length; i++) {
            if (esc.conditions[i].id == conditionId) {
                esc.conditions[i].satisfied = satisfied;
                esc.conditions[i].satisfiedAt = satisfied ? uint64(block.timestamp) : 0;
                emit ConditionUpdated(escrowId, conditionId, satisfied);
                return;
            }
        }
        revert("ESCROW:UNKNOWN_CONDITION");
    }

    function depositFunds(uint256 escrowId) external payable nonReentrant {
        DataTypes.Escrow storage esc = _requireEscrow(escrowId);
        require(esc.status == DataTypes.EscrowStatus.CREATED, "ESCROW:STATUS");
        require(_msgSender() == esc.buyer, "ESCROW:NOT_BUYER");

        if (esc.paymentToken == address(0)) {
            require(msg.value == esc.amount, "ESCROW:INVALID_VALUE");
        } else {
            require(msg.value == 0, "ESCROW:HBAR_NOT_ALLOWED");
            IERC20(esc.paymentToken).transferFrom(_msgSender(), address(this), esc.amount);
        }

        require(IComplianceOracle(complianceOracle).isCompliant(esc.buyer, esc.assetId), "ESCROW:BUYER_NON_COMPLIANT");
        require(IComplianceOracle(complianceOracle).isCompliant(esc.seller, esc.assetId), "ESCROW:SELLER_NON_COMPLIANT");

        esc.status = DataTypes.EscrowStatus.FUNDED;
        esc.fundedAt = uint64(block.timestamp);

        emit FundsDeposited(escrowId, _msgSender(), esc.amount);
    }

    function releaseFunds(uint256 escrowId) external nonReentrant onlyEscrowAgent {
        DataTypes.Escrow storage esc = _requireEscrow(escrowId);
        require(esc.status == DataTypes.EscrowStatus.FUNDED, "ESCROW:STATUS");
        require(_conditionsSatisfied(esc), "ESCROW:CONDITIONS_PENDING");

        esc.status = DataTypes.EscrowStatus.RELEASED;
        esc.releasedAt = uint64(block.timestamp);

        _payout(esc.seller, esc.paymentToken, esc.amount);
        emit FundsReleased(escrowId, esc.seller, esc.amount);
    }

    function refundBuyer(uint256 escrowId) external nonReentrant onlyEscrowAgent {
        DataTypes.Escrow storage esc = _requireEscrow(escrowId);
        require(esc.status == DataTypes.EscrowStatus.FUNDED, "ESCROW:STATUS");

        esc.status = DataTypes.EscrowStatus.REFUNDED;
        _payout(esc.buyer, esc.paymentToken, esc.amount);
        emit Refunded(escrowId, esc.buyer, esc.amount);
    }

    function _payout(address to, address paymentToken, uint256 amount) internal {
        if (paymentToken == address(0)) {
            (bool ok, ) = to.call{value: amount}("");
            require(ok, "ESCROW:HBAR_TRANSFER_FAIL");
        } else {
            IERC20(paymentToken).transfer(to, amount);
        }
    }

    function _conditionsSatisfied(DataTypes.Escrow storage esc) internal view returns (bool) {
        for (uint256 i = 0; i < esc.conditions.length; i++) {
            if (!esc.conditions[i].satisfied) {
                return false;
            }
        }
        return true;
    }

    function _requireEscrow(uint256 escrowId) internal view returns (DataTypes.Escrow storage) {
        DataTypes.Escrow storage esc = _escrows[escrowId];
        require(esc.buyer != address(0), "ESCROW:UNKNOWN");
        return esc;
    }
    receive() external payable {}
}

interface IComplianceOracle {
    function isCompliant(address user, uint256 propertyId) external view returns (bool);
}

